//query product order table for all those associated with this FO

var fou = new FMFOrderUtils();

var grProductOrder = new GlideRecord('x_lsmcb_fm_facility_product_order');
grProductOrder.addQuery('facility_order', current.sys_id + '');
grProductOrder.addNullQuery('facility_suborder');
grProductOrder.query();


if (!grProductOrder.next()) {
    gs.info('>>>>>>>! couldnt find product order without facility suborder and with facility order: ' + current.sys_id);
    //query item reservations for the current FO, to exclude them from the SubOrder Query
    var grItemReservation = new GlideRecord('x_lsmcb_fm_item_reservation');
    grItemReservation.addQuery('facility_order', current.sys_id + '');
    grItemReservation.query();
    if(!grItemReservation.next()) {
        gs.info('There is no item reservation');
        var grFSO = new GlideRecord('x_lsmcb_fm_facility_suborder');
        grFSO.addQuery('facility_order', current.sys_id + '');
        grFSO.query();
        
        if (!grFSO.next()) {
            gs.info('Cant find a facility suborder, so creating one.');
            //create a new Facility Suborder
            grFacilitySuborder = new GlideRecord('x_lsmcb_fm_facility_suborder');
            grFacilitySuborder.initialize();
            
            //populate all fields
            grFacilitySuborder.facility_order = current.sys_id + '';
            grFacilitySuborder.requester = current.requester + '';
            grFacilitySuborder.type = current.type + '';
            grFacilitySuborder.category = current.category + '';
            grFacilitySuborder.location = current.location + '';
            grFacilitySuborder.room = current.room + '';
            grFacilitySuborder.costcenter = current.costcenter + '';
            grFacilitySuborder.assignment_group = workflow.scratchpad.supplier_group + '';
            grFacilitySuborder.company = workflow.scratchpad.company + '';
            grFacilitySuborder.short_description = current.short_description + '';
            grFacilitySuborder.description = current.description + '';
            grFacilitySuborder.expected_start = current.expected_start;
            grFacilitySuborder.expected_end = current.expected_end;
            grFacilitySuborder.cmdb_ci = current.cmdb_ci + '';
            grFacilitySuborder.external_contact = current.external_contact + '';
            grFacilitySuborder.insert();
        }
    }
} else {
	gs.info('Found a product order!!!!')
    createFacilitySuborder();
}


//for each record

gs.info('Entering the while there are product orders.')
while (grProductOrder.next()) {
    createFacilitySuborder();
}

function createFacilitySuborder () {
    
    var company = grProductOrder.facility_product.company + '';
    var assignment_group;
    
    
    
    //query the supplier group assignment rule table
    var grSuppGrpAssignRule = new GlideRecord('x_lsmcb_fm_supplier_group_assignment_rules');
    grSuppGrpAssignRule.addQuery('location', current.location + '');
    grSuppGrpAssignRule.addQuery('type', current.type + '');
    grSuppGrpAssignRule.addQuery('category', current.category + '');
    grSuppGrpAssignRule.query();
    
    
    if (grSuppGrpAssignRule.next()) {
        assignment_group = grSuppGrpAssignRule.supplier_group + '';
        if (grSuppGrpAssignRule.auto_planning + '' == 'true') {
            workflow.scratchpad.auto_planning = 'true';
        }
        //if there is a vendor set in the assignment rule then override the value from the productorder
        if (grSuppGrpAssignRule.company) {
            company = grSuppGrpAssignRule.company + '';
        }
    }
    
    var itemReservationsSysIds = ['x'];
    //query item reservations for the current FO, to exclude them from the SubOrder Query
    var grItemReservation = new GlideRecord('x_lsmcb_fm_item_reservation');
    grItemReservation.addQuery('facility_order', current.sys_id + '');
    grItemReservation.query();
    while(grItemReservation.next()) {
        itemReservationsSysIds.push(grItemReservation.facility_suborder.sys_id + '');
    }
    var itemReservations = itemReservationsSysIds.join(',');
    
    //query Facility SubOrder table for the supplier & the Facility Order
    var grFacilitySuborder = new GlideRecord('x_lsmcb_fm_facility_suborder');
    grFacilitySuborder.addQuery('facility_order', grProductOrder.facility_order.sys_id + '');
    grFacilitySuborder.addQuery('company', company);
    grFacilitySuborder.addQuery('state', '!=', '4');
    grFacilitySuborder.addQuery('state', '!=', '6');
    grFacilitySuborder.addQuery('state', '!=', '7');
    grFacilitySuborder.addQuery('sys_id', 'NOT IN', itemReservations);
    grFacilitySuborder.query();
    
    
    //if no records are found, create a Facility Suborder for that Facility Order and Supplier
    if (!grFacilitySuborder.next()) {
        gs.info('Cant find a facility suborder. Creating a suborder')
        //create a new Facility Suborder
        grFacilitySuborder = new GlideRecord('x_lsmcb_fm_facility_suborder');
        grFacilitySuborder.initialize();
        
        //populate all fields
        grFacilitySuborder.facility_order = current.sys_id + '';
        grFacilitySuborder.company = company;
        grFacilitySuborder.requester = current.requester + '';
        grFacilitySuborder.type = current.type + '';
        grFacilitySuborder.category = current.category + '';
        grFacilitySuborder.location = current.location + '';
        grFacilitySuborder.room = current.room + '';
        grFacilitySuborder.costcenter = current.costcenter + '';
        grFacilitySuborder.assignment_group = workflow.scratchpad.supplier_group;
        grFacilitySuborder.description = current.description + '';
        grFacilitySuborder.short_description = current.short_description + '';
        grFacilitySuborder.expected_start = current.expected_start;
        grFacilitySuborder.expected_end = current.expected_end;
        grFacilitySuborder.cmdb_ci = current.cmdb_ci + '';
        grFacilitySuborder.external_contact = current.external_contact + '';
        grProductOrder.facility_suborder = grFacilitySuborder.insert() + '';
        
        // 		fou.addServicestoSuborder(grFacilitySuborder.sys_id.toString());
    } else {
		gs.info('There is a facility suborder. Adding it on the product order');
        //assign the Product Order to the newly created Suborder;
        grProductOrder.facility_suborder = grFacilitySuborder.sys_id + '';
    }
    
    grProductOrder.update();
    fou.createOrderlineCosts(grProductOrder);
}

