answer = isScript();
    
function isScript() {

	workflow.scratchpad.auto_planning = 'false';
	workflow.scratchpad.default_order_group = '';
	workflow.scratchpad.supplier_group = current.assignment_group + '';
	workflow.scratchpad.company = '';
    
	var grSuppGrpAssignRule = new GlideRecord('x_lsmcb_fm_supplier_group_assignment_rules');
	grSuppGrpAssignRule.addQuery('location', current.location + '');
	grSuppGrpAssignRule.addQuery('type', current.type + '');
	grSuppGrpAssignRule.addQuery('category', current.category + '');
	grSuppGrpAssignRule.query();
	if (grSuppGrpAssignRule.next()) {
		if (grSuppGrpAssignRule.company) {
			workflow.scratchpad.company = grSuppGrpAssignRule.company + '';
		}
        workflow.scratchpad.supplier_group = grSuppGrpAssignRule.supplier_group + '';
		if (grSuppGrpAssignRule.auto_planning + '' == 'true') {
			workflow.scratchpad.auto_planning = 'true';
			return 'yes';
		} else {
			return 'no';
		}
		
	} else {
		return 'no';
	}
}

