// Set the variable 'answer' to true or false to indicate if the condition has been met or not.
answer = checkOpenTasks();

function checkOpenTasks(){
	
	if(current.wf_action == 'go_back') {
		return true;
	}
	
	var grFacilitySuborderAll = new GlideRecord('x_lsmcb_fm_facility_suborder');
	grFacilitySuborderAll.addQuery('facility_order', current.sys_id + '');
	grFacilitySuborderAll.query();
	
	var grFacilitySuborder = new GlideRecord('x_lsmcb_fm_facility_suborder');
	grFacilitySuborder.addQuery('facility_order', current.sys_id + '');
	grFacilitySuborder.addQuery('state', 6).addOrCondition('state', 7).addOrCondition('state', 4);
	grFacilitySuborder.query();
	gs.info('counting grFacilitySuborder in WF: ' + grFacilitySuborder.getRowCount());
	if(grFacilitySuborderAll.getRowCount() == grFacilitySuborder.getRowCount()){
		return true;
	} else {
		return false;
	}
}

