	var grDefaultAssignGrp = new GlideRecord('x_lsmcb_fm_default_assignment');
	grDefaultAssignGrp.addQuery('location', current.location + '');
	grDefaultAssignGrp.query();
	if (grDefaultAssignGrp.next()) {
		current.assignment_group = grDefaultAssignGrp.assignment_group;
	} else {
		current.assignment_group = gs.getProperty('x_lsmcb_fm.default_order_assignment_group');
	}
}

